import React from 'react';
import Click from "./Click.js"

var Blur = require('react-blur').default;

class Overlay extends React.Component {
    constructor(props) {
        super(props);
    }

 render() {
     return(
        <React.Fragment>
                    <Blur ref="intro" img={Placeholder} className={this.state.intro ? "centerContainer" : "centerContainer overlay fadeOut"} blurRadius={50} style={{zIndex:3}} onClick={this.removeIntro.bind(this)} enableStyles>

                        <div className={this.state.loaded ? "fadeIn" : "invisible"}>
                            <Click height={100} width={100} />
                        </div>

                        <div className={!this.state.loaded ? "spinner" : "spinner spinnerFadeOut"}>
                            <ImpulseSpinner  size={50} frontColor="#686769" loading={true} />
                        </div>

                    </Blur>
        </React.Fragment>
      );
   }
}

export default Overlay;
