import React from "react";
import { Textfit } from "react-textfit";
import { ImpulseSpinner } from "react-spinners-kit";

class Scale extends React.Component {
  render() {
    return (
      <React.Fragment>
        <div className={this.props.loading ? "scaleBackground" : "scaleBackground fadeOut"}>
          <Textfit mode="single" className={"test"}>
            <div className={"scale"}>{this.props.scale}x</div>
          </Textfit>
        </div>
      </React.Fragment>
    );
  }
}

export default Scale;
