import React, { Component } from 'react';

class Fractal extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            x: 600,
            y: 400,
            iterations: 50,
            scale: 1
        };
    }

    handleClick = e => {
        e.persist();
        const canvas = this.refs.canvas;

        let rect = canvas.getBoundingClientRect(); // abs. size of element
        let scaleX = canvas.width / rect.width;    // relationship bitmap vs. element for X
        let scaleY = canvas.height / rect.height;  // relationship bitmap vs. element for Y
        let newX = (e.clientX - rect.left) * scaleX;   // scale mouse coordinates after they have
        let newY = (e.clientY - rect.top) * scaleY;     // been adjusted to be relative to element

        this.setState(prevState => {
          console.log(this.state.scale);
           return {
           x: prevState.x + (newX - 600)/this.state.scale,
           y: prevState.y + (newY - 400)/this.state.scale,
           iterations: prevState.iterations+=20,
           scale: prevState.scale*2}})
           this.forceUpdate();
    }

    componentDidMount() {
        const canvas = this.refs.canvas
        const ctx = canvas.getContext("2d")
        const img = this.refs.image

        img.onload = () => {
              ctx.drawImage(img, 0, 0)
          }
      }

    render() {
        var url = "http://localhost:8080/mandelbrot?x=" + this.state.x + "&y=" + this.state.y + "&scale=" + this.state.scale + "&iterations=" + this.state.iterations;
        console.log(url);

        return <div className="centerContainer">
                    <canvas ref="canvas" className="center" width={1200} height={800} onClick={this.handleClick.bind(this)}/>
                    <img className="center" ref="image" src={url} alt="new" style={{display: none}} />
                </div>
    }
}

export default Fractal;
