import React from "react";
import Click from "./Click.js";
import Placeholder from "./placeHolder.png";
import { ImpulseSpinner } from "react-spinners-kit";

class IntroOverlay extends React.Component {
constructor(props) {
    super(props);
    this.state = {
        rendered:false
    };
}

  imageLoaded() {
     this.setState({
       rendered: true
     })
  }


  render() {
    return (
      <div>

        <img ref="image"
        src={Placeholder}
        onLoad={this.imageLoaded.bind(this)}
        onClick={this.props.onClick}
        className={
            this.props.intro
            ? "introBlur"
            : "introBlur overlay fadeOut"
        }/>

        <div className={"centerContainer"} style={{zIndex:4}}>
                           <div
                             className={
                               this.props.loading
                                 ? "spinner introLoadFadeIn"
                                 : "spinner"
                             }
                           >
                             <ImpulseSpinner size={50} frontColor="#686769" loading={true} />
                           </div>
        </div>

                  <div
                    style={{ opacity: 0 }}
                    className={!this.props.loading && this.state.rendered ? "introMouseFadeIn" : ""}
                  >
                    <Click height={100} width={100} />
                  </div>

      </div>
    );
  }
}

export default IntroOverlay;
