import React from 'react';
import './styles.css'
import Overlay from "./Overlay.js"

var bigdecimal = require("bigdecimal");

class Fractal extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            width: 1920,        //the width of the fractal
            height: 1440,        //the height of the fractal
            x: new bigdecimal.BigDecimal(960 - 25),
            y: new bigdecimal.BigDecimal(720),
            i: 100,              //initial number of iterations
            scale: 1,            //initial scale
            intro: true,
            fade: false
        };
    }

    handleClick = e => {                                //zooms and focuses fractal on click
        const canvas = this.refs.canvas;
        let rect = canvas.getBoundingClientRect();      // abs. size of element
        let scaleX = new bigdecimal.BigDecimal(canvas.width / rect.width);         // relationship bitmap vs. element for X
        let scaleY = new bigdecimal.BigDecimal(canvas.height / rect.height);       // relationship bitmap vs. element for Y
        //let newX = (e.clientX - rect.left) * scaleX;    // scale mouse coordinates after they have
        //let newY = (e.clientY - rect.top) * scaleY;     // been adjusted to be relative to element
        let newX = new bigdecimal.BigDecimal((e.clientX - rect.left)).multiply(scaleX);
        let newY = new bigdecimal.BigDecimal((e.clientY - rect.top)).multiply(scaleY);

        this.setState(prevState => {

           return {
           //x: prevState.x + (newX - (this.state.width/2))/this.state.scale,
           x:  prevState.x.add((newX.subtract(new bigdecimal.BigDecimal(this.state.width/2.0))).divide(new bigdecimal.BigDecimal(this.state.scale))),
           //y: prevState.y + (newY - (this.state.height/2))/this.state.scale,
           y:  prevState.y.add((newY.subtract(new bigdecimal.BigDecimal(this.state.height/2.0))).divide(new bigdecimal.BigDecimal(this.state.scale))),
           i: prevState.i+=20,
           scale: prevState.scale*2}
        })
    }

    disableIntro() {
        this.setState({fade: true})
        console.log("fading out");
        setTimeout(() =>{
            this.setState({intro: false})
        }, 500)
    }


    componentDidMount() {                               //loads image to canvas to map bitmap to image for handleClick()
        let canvas = this.refs.canvas
        let ctx = canvas.getContext("2d")
        let ctxTwo = this.refs.canvasTwo.getContext("2d")
        let img = this.refs.image

        img.onload = () => {
            canvas.classList.remove("visible");
            ctxTwo.drawImage(img, 0, 0);
            canvas.classList.add("fadeOut");

            setTimeout(function(){
                ctx.drawImage(img, 0, 0);
                canvas.classList.remove("fadeOut");
                canvas.classList.add("visible");
            }, 500);
          }
      }

    render() {
        var url = "http://localhost:8080/mandelbrot?" +
                        "width=" + this.state.width +
                        "&height=" + this.state.height +
                        "&x=" + this.state.x +
                        "&y=" + this.state.y +
                        "&scale=" + this.state.scale +
                        "&i=" + this.state.i;


        return(
//        <LoadingOverlay
//          active={"true"}
//          spinner = {<SquareLoader/>}
//          >
    <React.Fragment>
        <div>

            <div onClick={this.disableIntro.bind(this)} className={this.state.fade? 'fadeOut centerContainer overlay': 'centerContainer overlay'}>
                <Overlay url={url} blur={50} />
             </div>
            
            <canvas ref="canvas" className="center" style={{display: 'block', zIndex: "1"}} width={this.state.width} height={this.state.height} onClick={this.handleClick.bind(this)}/>
            <canvas ref="canvasTwo" className="center" style={{display: 'block'}} width={this.state.width} height={this.state.height}/>
            <img className="center" ref="image" src={url} alt="new" style={{display: 'none'}} />;
        </div>
    </React.Fragment>
//        </LoadingOverlay>
        );
    }
}

export default Fractal;
