import React from "react";
import { Textfit } from "react-textfit";
import { CircleSpinner } from "react-spinners-kit";

class Scale extends React.Component {
  calculateScaleText() {
    return this.props.scale < 10000
      ? this.props.scale
      : this.props.scale.toExponential(1).replace(/e\+?/, '路10^');
  }

    calculateExponentText() {
      return this.props.scale < 10000
        ? ""
        : this.props.scale.toExponential(1).replace(/e\+?/, '路10^');
    }

  render() {
    return (
      <div
        className={
          !this.props.intro
            ? "visible introContainer noPointer"
            : "invisible introContainer noPointer"
        }
      >
        <div className={this.props.loading ? "" : "fadeOut"}>
          <div
            className={
              this.props.loading ? "scaleBackground scaleFadeOut" : "invisible"
            }
          >
            <Textfit mode="multi" className={"text"} max={56}>
              <div className={"scale"}>
                <p>{this.calculateScaleText()} <br/>
                <text style={{fontSize:18, marginTop:-15}}>{this.calculateExponentText()}</text> </p>
              </div>
            </Textfit>

          </div>

          <div
            className={
              this.props.loading
                ? "scaleSpinner center scaleSpinnerFadeIn"
                : "scaleSpinner center"
            }
            style={{ opacity: 0 }}
          >
            <CircleSpinner size={25} color="#FFFFFF" loading={true} />
          </div>
        </div>
      </div>
    );
  }
}

export default Scale;
