import React, { Component } from "react";
import Reload from "./icons8-restart.svg";

class Restart extends Component {
render() {
    return (

          <div ref="end" className={this.props.end && !this.props.loading ? "fadeIn scaleBackground" : " invisible overlay scaleBackground"} style={{zIndex: 4,cursor: "pointer"}}>

          <img src={Reload} alt="Restart from beginning." className={"reloadIcon"}/>


           </div>
)
}
}

export default Restart;
