import React from "react";
import { Textfit } from "react-textfit";
import { ImpulseSpinner } from "react-spinners-kit";

class Scale extends React.Component {
constructor(props) {
    super(props);
        this.state = {
          longWait: true
        };
 }

 componentDidUpdate(prevProps, prevState) {
     console.log("test");
   }

  render() {
    return (
      <React.Fragment>
        <div className={!this.state.longWait ? "scaleBackground" : "fadeOutStay"}>
          <Textfit mode="single" className={"test"}>
            <div className={"scale"}>{this.props.scale}x</div>
          </Textfit>
        </div>
        <div className={this.state.longWait ? "fadeIn" : "fadeOutStay"}>
          <ImpulseSpinner size={50} frontColor="#686769" loading={true} />
        </div>
      </React.Fragment>
    );
  }
}

export default Scale;
