import React from "react";
import ReactDOM from "react-dom";
import './styles.css'

class Image extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            x: 800,
            y: 600,
            scale: 1
        };
    }

    handleClick = e => {
        e.persist();


        const canvas = this.refs.canvas;
        var img = canvas.getContext("2d");

        this.setState(prevState => {
        console.log(e.screenX);
           return {
           x: e.screenX,
           y: e.screenY,
           scale: prevState.scale}})
            console.log(this.state.x);
           this.forceUpdate();
    }

    componentDidMount() {
        const canvas = this.refs.canvas
        const ctx = canvas.getContext("2d")
        const img = this.refs.image

        img.onload = () => {
              ctx.drawImage(img, 0, 0)
          }
      }

    render() {
        var url = "http://localhost:8080/mandelbrot?translateX=" + this.state.x + "&translateY=" + this.state.y + "&scale=" + this.state.scale;
        console.log(url);

        return <div className="centerContainer">
                    <canvas ref="canvas" className="center" width={1600} height={1200} onClick={this.handleClick.bind(this)}/>
                    <img className="center" ref="image" src={url} alt="new" display="none" />
                </div>
    }
}

const rootElement = document.getElementById("root");
ReactDOM.render( <Image />,  rootElement);
