import React from 'react';
import placeHolder from './placeHolderMandelbrot.jpg'

var Blur = require('react-blur').default;

class Overlay extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            blur: props.blur
        };
    }

     handleClick = e => {
            while(this.state.blur >= 0 ){
                   console.log(this.state.blur);
              setTimeout(() => {
                this.updateBlur();
        }, 100)
       }
     }

    updateBlur(){

         this.setState((prevState) => {
                                return {
                                  blur: prevState.blur - 1
                               }
                     });

     }

 render() {

        return(
        <div className="centerContainer" style={{zIndex: "2"}}>
              <Blur className='center'  img={placeHolder} blurRadius={this.state.blur} onClick={this.handleClick.bind(this)}>

              </Blur>
        </div>
        );
      }
}

export default Overlay;
