import React from "react";
import ReactDOM from "react-dom";
import './styles.css'

class Image extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            x: 0,
            y: 0,
            scale: 1
        };
    }

    _onMouseClick(e) {
        this.setState({
            x: e.screenX,
            y: e.screenY,
            scale: this.state.scale ++
        });
    }

    render() {
        return <div className="centerContainer"
                onClick={this._onMouseClick.bind(this)}>
                    <img className="center"
                    src="http://localhost:8080/mandelbrot"
                    alt="new" />
                </div>
    }
}

const rootElement = document.getElementById("root");
ReactDOM.render( <Image />,  rootElement);
