import React, { Component } from 'react'
import Burger from 'react-css-burger';

class Menu extends Component {
  state = {
    active: false,
    iterations: this.props.iterations,
    scale: this.props.scale
  };

  render() {
    return (
        <div>
      <Burger
        onClick={() => this.setState({ active: !this.state.active })}
        active={this.state.active}
        style={{zIndex:5}}
        scale={0.8}
      />
        <div ref="menu" className={this.state.active ? "menuAppear menuContent" : "menuDissapear menuContent"}>
        <text>{this.props.scale}x</text>
        <text>{this.props.iterations}</text>
         </div>
       </div>
    );
  }
}

export default Menu;
