//            {this.props.scale}X
//        </div>

//          <ScaleText >
//            <p className={'scale'}>{this.props.scale}</p>
//          </ScaleText>
 render() {
     return(

        <div className={'scaleBackground'}>
          <Textfit mode="single">
            <text className={'scale'}>{this.props.scale}</text>
          </Textfit>
        </div>

      );
   }
}

export default Scale;
