import React from 'react';
import ScaleText from "react-scale-text";

class Scale extends React.Component {
    constructor(props) {
        super(props);
    }

//       <div className={'scale'}>
//            <div className={'scaleContainer'}>
//            </div>
//            {this.props.scale}X
//        </div>

 render() {
     return(

        <div className={'scaleContainer'} style={{ width: "150px", height: "150px" }}>
          <ScaleText>
            <p className="child">{this.props.scale}</p>
          </ScaleText>
        </div>

      );
   }
}

export default Scale;
