import React from 'react';
import placeholder from './placeholder.jpg'

var Blur = require('react-blur').default;

class Overlay extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            blur: props.blur,
            fade: false
        };
    }

 render() {
     return(
        <div className="centerContainer" style={{zIndex: "2"}}>
           <Blur  className={this.props.fade ? 'center fadeOut': 'center' } img={this.props.url} blurRadius={this.props.blur}>
           </Blur>
        </div>
      );
   }
}

export default Overlay;
