import React from "react";
import { Textfit } from "react-textfit";
import { ImpulseSpinner } from "react-spinners-kit";

class Scale extends React.Component {
  calculateScaleText() {
    return this.props.scale < 10000
      ? this.props.scale
      : this.props.scale.toExponential(1).replace(/e\+?/, '·10^');
  }

  render() {
    return (
      <div
        className={
          !this.props.intro
            ? "visible introContainer noPointer"
            : "invisible introContainer noPointer"
        }
      >
        <div className={this.props.loading ? "" : "fadeOut"}>
          <div
            className={
              this.props.loading ? "scaleBackground scaleFadeOut" : "invisible"
            }
          >
            <Textfit mode="single" className={"text"} max={72}>
              <div className={"scale"}>{this.calculateScaleText()}</div>
            </Textfit>
          </div>

          <div
            className={
              this.props.loading
                ? "scaleSpinner center scaleSpinnerFadeIn"
                : "scaleSpinner center"
            }
            style={{ opacity: 0 }}
          >
            <ImpulseSpinner size={50} frontColor="#686769" loading={true} />
          </div>
        </div>
      </div>
    );
  }
}

export default Scale;
