import React from "react";
import Click from "./Click.js";
import Placeholder from "./placeHolder.png";
import { ImpulseSpinner } from "react-spinners-kit";

var Blur = require("react-blur").default;

class IntroOverlay extends React.Component {
constructor(props) {
    super(props);
    this.state = {
        rendered:false
    };
}

  imageLoaded() {
     this.setState({
       rendered: true
     })
  }


  render() {
    return (
      <div>

        <img ref="image"
        src={Placeholder}
        onLoad={this.imageLoaded.bind(this)}
        onClick={this.props.onClick}

         className={
                    this.props.intro
                      ? "introBlur centerContainer"
                      : "introBlur centerContainer overlay fadeOut"
                  }/>



                          <div
                            className={
                              this.props.loading
                                ? "spinner introLoadFadeIn"
                                : "spinner introLoadFadeOut"
                            }
                          >
                            <ImpulseSpinner size={50} frontColor="#686769" loading={true} />
                          </div>

                  <div
                    style={{ opacity: 0 }}
                    className={!this.props.loading && this.state.rendered ? "introMouseFadeIn" : ""}
                  >
                    <Click height={100} width={100} />
                  </div>

      </div>
    );
  }
}

export default IntroOverlay;
