import React from "react";
import Click from "./Click.js";
import Placeholder from "./placeHolder.png";
import { ImpulseSpinner } from "react-spinners-kit";

var Blur = require("react-blur").default;

class IntroOverlay extends React.Component {
constructor(props) {
    super(props);
    this.state = {
        rendered:false
    };
}

  componentDidMount() {
  console.log("test")
     this.setState({
             rendered: true
           });
  }


  render() {
    return (
      <div>
        <Blur
          ref="intro"
          img={Placeholder}
          className={
            this.props.intro
              ? "centerContainer"
              : "centerContainer overlay fadeOut"
          }
          onClick={this.props.onClick}
          blurRadius={50}
          style={{ zIndex: 3 }}
          enableStyles
        >

                  <div
                    className={
                      this.props.loading
                        ? "spinner introLoadFadeIn"
                        : "spinner introLoadFadeOut"
                    }
                  >
                    <ImpulseSpinner size={50} frontColor="#686769" loading={true} />
                  </div>

          <div
            style={{ opacity: 0 }}
            className={!this.props.loading ? "introMouseFadeIn" : ""}
          >
            <Click height={100} width={100} />
          </div>


        </Blur>
      </div>
    );
  }
}

export default IntroOverlay;
