import React from 'react';
import './styles.css'
import Overlay from "./Overlay.js"
import Scale from "./Scale.js"
import LoadingOverlay from 'react-loading-overlay';
import { FlagSpinner } from "react-spinners-kit";

var bigdecimal = require("bigdecimal");
var HALF_UP = bigdecimal.RoundingMode.HALF_UP();

class Fractal extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            width: 1920,        //the width of the fractal
            height: 1440,        //the height of the fractal
            x: new bigdecimal.BigDecimal(960 - 25),
            y: new bigdecimal.BigDecimal(720),
            i: 180,              //initial number of iterations
            scale: 1,            //initial scale
            intro: true,
            loading: true,
            loaded: false
        };
    }

    handleClick = e => {                                //zooms and focuses fractal on click
        const canvas = this.refs.canvas;
        let rect = canvas.getBoundingClientRect();      // abs. size of element
        
        let realWidth = (rect.width/rect.height < 4/3) ? (rect.height*4)/3 : rect.width;    //compensates for object-fit cover
        let realHeight = (rect.width/rect.height > 4/3) ? (rect.width*3)/4 : rect.height;
        let scaleX = canvas.width / realWidth;
        let scaleY = canvas.height / realHeight;
        let newX = (e.clientX - rect.width/2 + realWidth/2 - rect.left) * scaleX;
        let newY = (e.clientY - rect.height/2 + realHeight/2 - rect.top) * scaleY;


//need to convert this
        this.setState(prevState => {
           return {
            x: prevState.x + (newX - (this.state.width/2.0))/this.state.scale,
            y: prevState.y + (newY - (this.state.height/2.0))/this.state.scale,
           i: prevState.i+=10,
           scale: prevState.scale*2,
           intro: false}
        })

    }

    imageLoaded(){

                this.setState({loaded:true,
                loading: false
                })
    }

    componentDidMount() {                               //loads image to canvas to map bitmap to image for handleClick()
        let canvas = this.refs.canvas
        let ctx = canvas.getContext("2d")
        let ctxTwo = this.refs.canvasTwo.getContext("2d")
        let img = this.refs.image

        img.onload = () => {
            canvas.classList.remove("visible");
            ctxTwo.drawImage(img, 0, 0);
            canvas.classList.add("fadeOut");

            setTimeout(function(){
                ctx.drawImage(img, 0, 0);
                canvas.classList.remove("fadeOut");
                canvas.classList.add("visible");
            }, 500);
          }
      }

//    removeFade() {
//        let scale = this.refs.scale;
//        scale.classList.remove('scaleAnimation');
//        console.log("hit!");
//    }

    render() {
        var url = "http://localhost:8080/mandelbrot?" +
                        "width=" + this.state.width +
                        "&height=" + this.state.height +
                        "&x=" + this.state.x +
                        "&y=" + this.state.y +
                        "&scale=" + this.state.scale +
                        "&i=" + this.state.i;

    console.log(url);
        return(

    <React.Fragment>
            <canvas ref="canvas" className="center" style={{display: 'block', zIndex: "1"}} width={this.state.width} height={this.state.height} onClick={this.handleClick.bind(this)}/>
            <canvas ref="canvasTwo" className="center" style={{display: 'block'}} width={this.state.width} height={this.state.height}/>
            <img onLoad={this.imageLoaded.bind(this)} className="center" ref="image" src={url} alt="new" style={{display: 'none'}} />;

    </React.Fragment>
        );
    }
}

export default Fractal;
