import React from "react";
import ReactDOM from "react-dom";
import './styles.css'

class Image extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            x: 0,
            y: 0,
            scale: 1
        };
    }

    handleClick = e => {
        e.persist();

        this.setState(prevState => {
                console.log((e.screenX-800)/(prevState.scale + 1) + " " + (e.screenY-600)/(prevState.scale + 1));
           return {
           x: (e.screenX-800)/(prevState.scale + 1),
           y: (e.screenY-600)/(prevState.scale + 1),
           scale: prevState.scale + 1}})
            console.log(this.state.x);
           this.forceUpdate();
    }

    render() {
        var url = "http://localhost:8080/mandelbrot?translateX=" + this.state.x + "&translateY=" + this.state.y + "&scale=" + this.state.scale;
        console.log(url);
        return <div className="centerContainer"
                onClick={this.handleClick.bind(this)}>
                    <img className="center"
                    src={url}
                    alt="new" />
                </div>
    }
}

const rootElement = document.getElementById("root");
ReactDOM.render( <Image />,  rootElement);
