import React from 'react';
import Click from "./Click.js"
import Placeholder from "./placeHolder.png";
import { ImpulseSpinner } from "react-spinners-kit";

var Blur = require('react-blur').default;

class IntroOverlay extends React.Component {
    constructor(props) {
        super(props);
//        this.state = {
//            intro: this.props.intro,
//            loaded: this.props.loaded
//        };
    }

 render() {
     return(
        <div>
           <Blur ref="intro" img={Placeholder} className={this.props.intro ? "centerContainer" : "centerContainer overlay fadeOut"} blurRadius={50} style={{zIndex:3}} enableStyles>
             <div className={this.props.loaded ? "fadeIn" : "invisible"}>
                <Click height={100} width={100} />
             </div>

             <div className={!this.props.loaded ? "spinner" : "spinner spinnerFadeOut"}>
                <ImpulseSpinner  size={50} frontColor="#686769" loading={true} />
             </div>

           </Blur>
        </div>
      );
   }
}

export default IntroOverlay;
