import React from 'react';
import placeholder from './placeholder.jpg'

var Blur = require('react-blur').default;

class Overlay extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            blur: props.blur,
            fade: false
        };
    }

     handleClick = e => {
         this.setState(prevState => {
             return {
                   fade: true
             }})
     }

 render() {
     return(
        <div className="centerContainer" style={{zIndex: "2"}} onClick={this.props.function}>
           <Blur className={this.state.fade ? 'center fadeOut': 'center'} img={this.props.url} blurRadius={this.props.blur} onClick={this.handleClick.bind(this)}>
           </Blur>
        </div>
      );
   }
}

export default Overlay;
