import React from 'react';
import Fade from '@material-ui/core/Fade';

var bigdecimal = require("bigdecimal");

class Fractal extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            width: 1280,        //the width of the fractal
            height: 720,        //the height of the fractal
            //x: 640,
            x: new bigdecimal.BigDecimal(640),             //the center x-coordinate of the fractal
            //y: 360,             //the center y-coordinate of the fractal
            y: new bigdecimal.BigDecimal(360),
            i: 50,              //initial number of iterations
            scale: 1            //initial scale
        };
    }

    handleClick = e => {                                //zooms and focuses fractal on click
        const canvas = this.refs.canvas;
        let rect = canvas.getBoundingClientRect();      // abs. size of element
        let scaleX = canvas.width / rect.width;         // relationship bitmap vs. element for X
        let scaleY = canvas.height / rect.height;       // relationship bitmap vs. element for Y
        //let newX = (e.clientX - rect.left) * scaleX;    // scale mouse coordinates after they have
        //let newY = (e.clientY - rect.top) * scaleY;     // been adjusted to be relative to element


        let newX = new bigdecimal.BigDecimal((e.clientX - rect.left) * scaleX);
        let newY = new bigdecimal.BigDecimal((e.clientY - rect.left) * scaleY);

        this.setState(prevState => {


           return {
           //x: prevState.x + (newX - (this.state.width/2))/this.state.scale,
           x:  prevState.x.add(newX.subtract(new bigdecimal.BigDecimal(this.state.width/2.0)).divide(new bigdecimal.BigDecimal(this.state.scale))),
           //y: prevState.y + (newY - (this.state.height/2))/this.state.scale,
           y:  prevState.y.add(newY.subtract(new bigdecimal.BigDecimal(this.state.height/2.0)).divide(new bigdecimal.BigDecimal(this.state.scale))),
           i: prevState.i+=20,
           scale: prevState.scale*2}
        })
    }

    componentDidMount() {                               //loads image to canvas to map bitmap to image for handleClick()
        let canvas = this.refs.canvas
        let ctx = canvas.getContext("2d")
        let img = this.refs.image

        img.onload = () => {
              ctx.drawImage(img, 0, 0)
          }
      }

    render() {
        var url = "http://localhost:8080/mandelbrot?" +
                        "width=" + this.state.width +
                        "&hight=" + this.state.height +
                        "&x=" + this.state.x +
                        "&y=" + this.state.y +
                        "&scale=" + this.state.scale +
                        "&i=" + this.state.i;

        console.log(url);
        return( <Fade in={true} style={'block'}>
        <div>
        <div className="centerContainer">
                <canvas ref="canvas" className="center" width={this.state.width} height={this.state.height} onClick={this.handleClick.bind(this)}/>
                <img className="center" ref="image" src={url} alt="new" style={{display: 'none'}} />
        </div>
        </div>
        </Fade>);
    }
}

export default Fractal;
