import React from "react";
import { Textfit } from "react-textfit";
import { ImpulseSpinner } from "react-spinners-kit";

class Scale extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      showLoading:false,

    }
    }


  render() {
    return (
     <div
               className={(!this.props.intro) ? "visible centerContainer overlay" : "invisible centerContainer overlay"}
               style={{ zIndex:4}}
               >
        <div className={this.props.loading ? "scaleBackground" : "scaleBackground fadeOut"} style={{zIndex:5}}>
          <Textfit mode="single" className={"test"}>
            <div className={"scale"}>{this.props.scale}x</div>
          </Textfit>
        </div>

         <div className={this.props.loading ? "scaleSpinner center spinnerFadeIn" : "scaleSpinner center"} style={{opacity:0}}>
                 <ImpulseSpinner size={50} frontColor="#686769" loading={true} />
        </div>


      </div>
    );
  }
}

export default Scale;
