import React from "react";
import { Textfit } from "react-textfit";
import { ImpulseSpinner } from "react-spinners-kit";

class Scale extends React.Component {
  render() {
    return (
      <React.Fragment>
        <div className={this.props.loading ? "scaleBackground" : "scaleBackground scaleFadeOut"}>
          <Textfit mode="single" className={"test"}>
            <div className={"scale"}>{this.props.scale}x</div>
          </Textfit>
        </div>

         <div className={this.props.loading ? "scaleSpinner center spinnerFadeIn" : "scaleSpinner center fadeOut"} style={{opacity:0}}>
                 <ImpulseSpinner size={50} frontColor="#686769" loading={true} />
        </div>


      </React.Fragment>
    );
  }
}

export default Scale;
