import React from "react";
import { Textfit } from "react-textfit";
import { ImpulseSpinner } from "react-spinners-kit";

class Scale extends React.Component {
  calculateScaleText() {
    let scaleText = (this.props.scale).toString(10);
    return scaleText < 10000
      ? scaleText + "x"
      : "<p>" + scaleText.substring(0, 2) + "·10" + (scaleText.length-1).toString(10).sup() + "x</p>";
  }

  render() {
    return (
      <div
        className={
          !this.props.intro
            ? "visible introContainer noPointer"
            : "invisible introContainer noPointer"
        }
      >
        <div className={this.props.loading ? "" : "fadeOut"}>
          <div
            className={
              this.props.loading ? "scaleBackground scaleFadeOut" : "invisible"
            }
          >
            <Textfit mode="single" className={"text"}>
              <div className={"scale"}><p>{this.calculateScaleText()}</p></div>
            </Textfit>
          </div>

          <div
            className={
              this.props.loading
                ? "scaleSpinner center scaleSpinnerFadeIn"
                : "scaleSpinner center"
            }
            style={{ opacity: 0 }}
          >
            <ImpulseSpinner size={50} frontColor="#686769" loading={true} />
          </div>
        </div>
      </div>
    );
  }
}

export default Scale;
