import React, { Component } from 'react'
import Burger from 'react-css-burger';
import { Textfit } from 'react-textfit';


class Menu extends Component {
  state = {
    active: false,
    inputField: ''
  };

submitHandler(evt) {
    evt.preventDefault();
    // pass the input field value to the event handler passed
    // as a prop by the parent (App)
    this.props.handlerFromParant(this.state.inputField);

    this.setState({
      inputField: this.props.iterations
    });
  }

  render() {
    return (
        <div>
      <Burger
        onClick={() => this.setState({ active: !this.state.active })}
        active={this.state.active}
        style={{zIndex:5}}
        scale={0.8}
      />
        <div ref="menu" className={this.state.active ? "menuAppear menuContent" : "menuDissapear menuContent"}>
        <form onSubmit={this.submitHandler}>
        <text className={'menuText'}>Scale: {this.props.scale}x</text>
                  <input type="text"
                         id="theInput"
                         value={this.state.inputField}
                         onChange={this.handleChange} />
                  <input type="submit" />
                </form>

        <Textfit mode="single" className={"test"} max={56}>



            </Textfit>
         </div>
       </div>
    );
  }
}

export default Menu;
