import React from 'react';
import './styles.css'
import Overlay from "./Overlay.js"
import Scale from "./Scale.js"
import LoadingOverlay from 'react-loading-overlay';
import { FlagSpinner } from "react-spinners-kit";

var bigdecimal = require("bigdecimal");
var HALF_UP = bigdecimal.RoundingMode.HALF_UP();

class Fractal extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            width: 1920,        //the width of the fractal
            height: 1440,        //the height of the fractal
//            x: new bigdecimal.BigDecimal(960 - 25),
//            y: new bigdecimal.BigDecimal(720),
            x: 960 - 25,
            y: 720,
            i: 180,              //initial number of iterations
            scale: 1,            //initial scale
            intro: true,
            loading: true,
            loaded: false
        };
    }

    handleClick = e => {                                //zooms and focuses fractal on click
        const canvas = this.refs.canvas;
        let rect = canvas.getBoundingClientRect();      // abs. size of element

        let rectWidth = new bigdecimal.BigDecimal(rect.width);
        let rectHeight = new bigdecimal.BigDecimal(rect.height);
        let rectLeft = new bigdecimal.BigDecimal(rect.left);
        let rectRight = new bigdecimal.BigDecimal(rect.right);
        let rectTop = new bigdecimal.BigDecimal(rect.top);
        let clientX = new bigdecimal.BigDecimal(e.clientX);
        let clientY = new bigdecimal.BigDecimal(e.clientY);
        let canvasWidth = new bigdecimal.BigDecimal(canvas.width);

//        let scaleX = new bigdecimal.BigDecimal(canvas.width).divide(rectWidth,20, HALF_UP);         // relationship bitmap vs. element for X
//        let scaleY = new bigdecimal.BigDecimal(canvas.height).divide(rectHeight,20, HALF_UP);       // relationship bitmap vs. element for Y

        let scaleX = canvas.width / rect.width;    // relationship bitmap vs. element for X
        let scaleY = canvas.height / rect.height;

        if(rect.width/rect.height < 4/3) {
            console.log("shrinking y")
        }

        let newX = (e.clientX - rect.left) * scaleX;    // scale mouse coordinates after they have
        let newY = (e.clientY - rect.top) * scaleY;     // been adjusted to be relative to element

//       let newX = (e.clientX - rect.left) / (rect.right - rect.left) * canvas.width;
//        let newY = (e.clientY - rect.top) / (rect.bottom - rect.top) * canvas.height;

//        let newX = (new bigdecimal.BigDecimal(e.clientX).subtract(rectLeft)).multiply(scaleX);
//        let newY = (new bigdecimal.BigDecimal(e.clientY).subtract(rectTop)).multiply(scaleY);



//https://stackoverflow.com/questions/17130395/real-mouse-position-in-canvas/33063222#33063222
//https://stackoverflow.com/questions/17130395/real-mouse-position-in-canvas
//        const scale = this.refs.scale;
//        scale.classList.add("scaleAnimation:hover");

        this.setState(prevState => {

           return {
            x: prevState.x + (newX - (this.state.width/2.0))/this.state.scale,
            y: prevState.y + (newY - (this.state.height/2.0))/this.state.scale,
           i: prevState.i+=10,
           scale: prevState.scale*2,
           intro: false}
        })

    }

    imageLoaded(){

                this.setState({loaded:true,
                loading: false
                })
    }

    componentDidMount() {                               //loads image to canvas to map bitmap to image for handleClick()
        let canvas = this.refs.canvas
        let ctx = canvas.getContext("2d")
//        let ctxTwo = this.refs.canvasTwo.getContext("2d")
        let img = this.refs.image

        img.onload = () => {
//            canvas.classList.remove("visible");
//            ctxTwo.drawImage(img, 0, 0);
//            canvas.classList.add("fadeOut");

//            setTimeout(function(){
                ctx.drawImage(img, 0, 0);
//                canvas.classList.remove("fadeOut");
//                canvas.classList.add("visible");
//            }, 500);
          }
      }

//    removeFade() {
//        let scale = this.refs.scale;
//        scale.classList.remove('scaleAnimation');
//        console.log("hit!");
//    }

    render() {
        var url = "http://localhost:8080/mandelbrot?" +
                        "width=" + this.state.width +
                        "&height=" + this.state.height +
                        "&x=" + this.state.x +
                        "&y=" + this.state.y +
                        "&scale=" + this.state.scale +
                        "&i=" + this.state.i;

//    console.log(url);
        return(

    <React.Fragment>



            <canvas ref="canvas" className="center" style={{display: 'block', zIndex: "1"}} width={this.state.width} height={this.state.height} onClick={this.handleClick.bind(this)}/>
//            <canvas ref="canvasTwo" className="center" style={{display: 'block'}} width={this.state.width} height={this.state.height}/>
            <img onLoad={this.imageLoaded.bind(this)} className="center" ref="image" src={url} alt="new" style={{display: 'none'}} />;

    </React.Fragment>
        );
    }
}

export default Fractal;
